"use client";

import Image from "next/image";
import { FaWhatsapp } from "react-icons/fa";

interface TravelAdvisor {
  id: number;
  name: string;
  designation: string;
  experience: string;
  trip_success: string;
  whatsapp: string;
  image: string;
}

interface TravelAdvisorProps {
  advisor: TravelAdvisor | null;
}

