  const openWhatsApp = () => {
    if (advisor?.whatsapp) {
      const formattedNumber = advisor.whatsapp.replace(/\D/g, "");
      const whatsappUrl = `https://wa.me/${formattedNumber}`;
      window.open(whatsappUrl, "_blank");
    } else {
      const defaultNumber = "";
      const whatsappUrl = `https://wa.me/${defaultNumber}`;
      window.open(whatsappUrl, "_blank");
    }
  };

